-- | Server or client configuration, specifying the host and port to query or serve on.
data ServerConfig = ServerConfig
  { configHost :: String  -- ^ Hostname to serve on, e.g. "127.0.0.1"
  , configPort :: Int      -- ^ Port to serve on, e.g. 8080
  } deriving (Eq, Ord, Show, Read)

-- | List of elements parsed from a query.
newtype QueryList (p :: CollectionFormat) a = QueryList
  { fromQueryList :: [a]
  } deriving (Functor, Applicative, Monad, Foldable, Traversable)

-- | Formats in which a list can be encoded into a HTTP path.
data CollectionFormat
  = CommaSeparated -- ^ CSV format for multiple parameters.
  | SpaceSeparated -- ^ Also called "SSV"
  | TabSeparated -- ^ Also called "TSV"
  | PipeSeparated -- ^ `value1|value2|value2`
  | MultiParamArray -- ^ Using multiple GET parameters, e.g. `foo=bar&foo=baz`. Only for GET params.

instance FromHttpApiData a => FromHttpApiData (QueryList 'CommaSeparated a) where
  parseQueryParam = parseSeparatedQueryList ','

instance FromHttpApiData a => FromHttpApiData (QueryList 'TabSeparated a) where
  parseQueryParam = parseSeparatedQueryList '\t'

instance FromHttpApiData a => FromHttpApiData (QueryList 'SpaceSeparated a) where
  parseQueryParam = parseSeparatedQueryList ' '

instance FromHttpApiData a => FromHttpApiData (QueryList 'PipeSeparated a) where
  parseQueryParam = parseSeparatedQueryList '|'

instance FromHttpApiData a => FromHttpApiData (QueryList 'MultiParamArray a) where
  parseQueryParam = error "unimplemented FromHttpApiData for MultiParamArray collection format"

parseSeparatedQueryList :: FromHttpApiData a => Char -> Text -> Either Text (QueryList p a)
parseSeparatedQueryList char = fmap QueryList . mapM parseQueryParam . T.split (== char)

instance ToHttpApiData a => ToHttpApiData (QueryList 'CommaSeparated a) where
  toQueryParam = formatSeparatedQueryList ','

instance ToHttpApiData a => ToHttpApiData (QueryList 'TabSeparated a) where
  toQueryParam = formatSeparatedQueryList '\t'

instance ToHttpApiData a => ToHttpApiData (QueryList 'SpaceSeparated a) where
  toQueryParam = formatSeparatedQueryList ' '

instance ToHttpApiData a => ToHttpApiData (QueryList 'PipeSeparated a) where
  toQueryParam = formatSeparatedQueryList '|'

instance ToHttpApiData a => ToHttpApiData (QueryList 'MultiParamArray a) where
  toQueryParam = error "unimplemented ToHttpApiData for MultiParamArray collection format"

formatSeparatedQueryList :: ToHttpApiData a => Char ->  QueryList p a -> Text
formatSeparatedQueryList char = T.intercalate (T.singleton char) . map toQueryParam . fromQueryList


-- | Backend for OpenFEC.
-- The backend can be used both for the client and the server. The client generated from the OpenFEC Swagger spec
-- is a backend that executes actions by sending HTTP requests (see @createOpenFECClient@). Alternatively, provided
-- a backend, the API can be served using @runOpenFECServer@.
data OpenFECBackend m = OpenFECBackend
  { auditCaseGet :: Maybe Text -> Maybe (QueryList 'MultiParamArray (Int)) -> Maybe Int -> Maybe (QueryList 'MultiParamArray (Text)) -> Maybe Text -> Maybe (QueryList 'MultiParamArray (Text)) -> Maybe Int -> Maybe Int -> Maybe Bool -> Maybe (QueryList 'MultiParamArray (Text)) -> Maybe (QueryList 'MultiParamArray (Text)) -> Maybe Int -> Maybe Text -> Maybe Text -> Maybe (QueryList 'MultiParamArray (Text)) -> Maybe (QueryList 'MultiParamArray (Int)) -> Maybe Bool -> Maybe (QueryList 'MultiParamArray (Text)) -> Maybe (QueryList 'MultiParamArray (Text)) -> m AuditCasePage{- ^  This endpoint contains Final Audit Reports approved by the Commission since inception. The search can be based on information about the audited committee (Name, FEC ID Number, Type,  Election Cycle) or the issues covered in the report.  -}
  , auditCategoryGet :: Maybe (QueryList 'MultiParamArray (Text)) -> Maybe Bool -> Maybe Text -> Maybe Bool -> Maybe (QueryList 'MultiParamArray (Text)) -> Maybe Int -> Maybe Int -> Maybe Text -> m AuditCategoryPage{- ^  This lists the options for the categories and subcategories available in the /audit-search/ endpoint.  -}
  , auditPrimaryCategoryGet :: Maybe (QueryList 'MultiParamArray (Text)) -> Maybe Bool -> Maybe Text -> Maybe Bool -> Maybe (QueryList 'MultiParamArray (Text)) -> Maybe Int -> Maybe Int -> Maybe Text -> m AuditPrimaryCategoryPage{- ^  This lists the options for the primary categories available in the /audit-search/ endpoint.  -}
  , namesAuditCandidatesGet :: Maybe Text -> Maybe (QueryList 'MultiParamArray (Text)) -> m AuditCandidateSearchList{- ^  Search for candidates or committees by name. If you're looking for information on a particular person or group, using a name to find the `candidate_id` or `committee_id` on this endpoint can be a helpful first step.  -}
  , namesAuditCommitteesGet :: Maybe Text -> Maybe (QueryList 'MultiParamArray (Text)) -> m AuditCommitteeSearchList{- ^  Search for candidates or committees by name. If you're looking for information on a particular person or group, using a name to find the `candidate_id` or `committee_id` on this endpoint can be a helpful first step.  -}
  , candidateCandidateIdGet :: Text -> Maybe (QueryList 'MultiParamArray (Text)) -> Maybe Int -> Maybe Text -> Maybe Bool -> Maybe Bool -> Maybe (QueryList 'MultiParamArray (Text)) -> Maybe (QueryList 'MultiParamArray (Int)) -> Maybe (QueryList 'MultiParamArray (Text)) -> Maybe (QueryList 'MultiParamArray (Text)) -> Maybe Int -> Maybe (QueryList 'MultiParamArray (Text)) -> Maybe (QueryList 'MultiParamArray (Text)) -> Maybe Text -> Maybe Bool -> Maybe (QueryList 'MultiParamArray (Int)) -> Maybe (QueryList 'MultiParamArray (Text)) -> Maybe Bool -> Maybe Text -> m CandidateDetailPage{- ^  This endpoint is useful for finding detailed information about a particular candidate. Use the `candidate_id` to find the most recent information about that candidate.   -}
  , candidateCandidateIdHistoryCycleGet :: Text -> Int -> Maybe Bool -> Maybe Text -> Maybe Bool -> Maybe Int -> Maybe Int -> Maybe Bool -> Maybe Text -> m CandidateHistoryPage{- ^  Find out a candidate's characteristics over time. This is particularly useful if the candidate runs for the same office in different districts or you want to know more about a candidate's previous races.  This information is organized by `candidate_id`, so it won't help you find a candidate who ran for different offices over time; candidates get a new ID for each office.  -}
  , candidateCandidateIdHistoryGet :: Text -> Maybe Bool -> Maybe Text -> Maybe Bool -> Maybe Int -> Maybe Int -> Maybe Bool -> Maybe Text -> m CandidateHistoryPage{- ^  Find out a candidate's characteristics over time. This is particularly useful if the candidate runs for the same office in different districts or you want to know more about a candidate's previous races.  This information is organized by `candidate_id`, so it won't help you find a candidate who ran for different offices over time; candidates get a new ID for each office.  -}
